    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        long añostrab, bonospag=100000, bonospag2=120000, op1, op2;
        String res, res2;
        
        if (txtAñosTrab.getText().trim().isEmpty())
        {
            JOptionPane.showMessageDialog(this, "Ingrese Años de Trabajo", "ERROR", JOptionPane.ERROR_MESSAGE);
            txtAñosTrab.requestFocusInWindow();
        }
        else {
        añostrab= Long.parseLong(txtAñosTrab.getText());
        
        if (añostrab==1)
        {
           op1= añostrab*bonospag;
           res= String.valueOf(op1);
           txtBonospag.setText(res);
        }
        if (añostrab>1)
        {
           op2= (añostrab*bonospag2)-20000; 
           res2= String.valueOf(op2);
           txtBonospag.setText(res2);
        }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

