    private void txtAñosTrabKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAñosTrabKeyTyped
        // TODO add your handling code here:
        char c=evt.getKeyChar(); 
          if(!Character.isDigit(c)) 
          { 
              getToolkit().beep(); 
              evt.consume();      
          }
         
    }//GEN-LAST:event_txtAñosTrabKeyTyped

    private void txtBonospagKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtBonospagKeyTyped
        // TODO add your handling code here:
        char c=evt.getKeyChar(); 
          if(!Character.isDigit(c)) 
          { 
              getToolkit().beep(); 
              evt.consume();      
          }
         
          if(!Character.isLetter(c)) 
          { 
              getToolkit().beep(); 
              evt.consume(); 
          }
    }//GEN-LAST:event_txtBonospagKeyTyped

